$(function(){
 $('.bachata-contact')
 .on('click',function(){
   $('.contact-form').toggle('slow');
});
});


$(function(){
$('header').on('click',function(event){
event.preventDefault();
var $this=$(this);

$this.parents().siblings()
.removeClass('.selected')
.end()
.addClass('selected');

var imagePath = $this.data('img');
$('.gallery .mainimage img').attr('src',imagePath);

})
});


$(function(){
$('.accordin')
.on('click','li',function(){
var $this=$(this);

$this
.toggleClass('expanded')
.children('.content')
.slideToggle('fast')
.end()
.siblings()
.removeClass('.expanded')
.children('.content')
.slideUp('fast');
});
});


$(function(){
var intervalId;
setTimer();
function setTimer(){
intervalId = setInterval(autoClick,2500);
}

function autoClick(){
$('.slide').children('a.next').click();
}

function changeImage($click){
var $current = $click.siblings('.container').children('.current');
var $new;
var findSelector='';
if($click.hasClass('next')){
$new = $current.next();
findSelector = ':first-child';
}else{
$new = $current.prev();
findSelector = ':last-child';
}
if($new.length==0){
$new = $current.siblings(findSelector);
}
$current.removeClass('current');
$new.addClass('current');
setTimer();
}
$('.slide')
.on('click','>a',function(event){
event.preventDefault();
clearInterval(intervalId);
changeImage($(this));
});
});


$(function(){
$('a .link').on('click',function(event){
event.preventDefault();
var $this= $(this);
var linkTo = $this.attr('href');
var $target = $(linkTo);
var offset = $target.data('offsettop');
var pos = $target.offset().top();
$('html,body').animate({scrollTop:pos},400);
});
});
